<template>
  <div class="activities-progression">
    <div class="activities-level">
      <span>{{this.getProgress() }}</span>
      <span>{{this.getTotal() }}</span>
    </div>

    <p class="activities-currentLevel">{{this.getLevel()}}</p>

    <h1>Bravo t'as progressé</h1>

    <p>Il te reste <strong>{{this.getSteps()}} mots</strong> à découvrir pour passer au niveau {{this.getNext()}}
      <svg class="activities" width="150" height="12" viewBox="0 0 150 12" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
        <mask id="mask0" mask-type="alpha" maskUnits="userSpaceOnUse" x="0" y="0" width="150" height="12">
          <rect width="150" height="12" fill="#C1F1FF"/>
        </mask>
        <g mask="url(#mask0)">
          <rect width="148" height="12" fill="url(#pattern0)"/>
        </g>
        <defs>
          <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
            <use xlink:href="#image0" transform="translate(0 -0.0065024) scale(0.00102669 0.0126626)"/>
          </pattern>
          <image id="image0" width="974" height="80" xlink:href="data:image/png;base64,"/>
        </defs>
      </svg>

    </p>


    <CustomButton
      @click.native="hideActivityPanel"
      arrow-color="#FF6644"
      color="white"
      text="Continuer"
    ></CustomButton>

  </div>
</template>

<script lang="ts">
import {Component, getModule, Vue} from 'nuxt-property-decorator'
import GlobalSceneStore from "~/store/globalScene"
import ActivityStore from "~/store/activity"
import CustomButton from "~/components/buttons/CustomButton.vue";
import GlobalScene from "~/core/scene/GlobalScene";
import GlobalStore from "~/store/global";
import {ProgressPercentManager} from "~/core/managers";

@Component({
  components:{
    CustomButton
  }
})
export default class ActivitiesProgression extends Vue {
  public globalSceneStore = getModule(GlobalSceneStore, this.$store)
  public activityStore = getModule(ActivityStore, this.$store)
  public globalStore = getModule(GlobalStore, this.$store)

  hideActivityPanel(){
    GlobalScene.context.resume()
    this.activityStore.setCurrentActivity(null)
    this.activityStore.hideActivityPanel()
    this.$router.push(this.globalSceneStore.activeObject!.room().fullUrl)
  }

  mounted(){
    this._initProgressManager()
  }

  private _initProgressManager() {
    ProgressPercentManager.words = this.globalStore.dataWord.length
    ProgressPercentManager.userAchievedWords = this.globalStore.achievedWords.length

  }

  public getLevel() {
    return ProgressPercentManager.current!.name
  }

  public getProgress() {
    ProgressPercentManager.init()
    return this.globalStore.achievedWords.length
  }

  public getTotal() {
    ProgressPercentManager.init()
    return this.globalStore.dataWord.length
  }

  public getNext() {
    return ProgressPercentManager.next?.name
  }

  public getSteps(){
    return Math.round(ProgressPercentManager.steps)
  }

}
</script>

